import axios from 'axios';
import React,{useState,useEffect} from 'react'
import "../CSS/Admin.css";
function Admin() {
    const [name, setName] = useState("")
    const [price, setprice] = useState("");
    const [image, setImage] = useState("")
    const [description, setDescription] = useState("")
    const [products, setProducts] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:5000/api/Product').then((res)=>{
        setProducts(res.data)
        console.log(products)
      })
    }, [])
    

    const Handlesubmit =()=>{
        axios.post('http://localhost:5000/api/Product',{
            name,
            price,
            image,
            description
        }).then((res)=>{
            console.log(res)
        })
    }

    const Delete = (id)=>{
        axios.delete(`http://localhost:5000/api/Product/${id}`).thes((res)=>{
            window.location.reload();
        })
    }

  return (
    <div className='admin-container'>

        <div className='admin-left'>
        
        <div>
        {products.map((product)=>{
        return(
            <div className='main-table'>
                <table class="table table-dark">

   <tr>
    <th>Image</th>
    <th>Product Name</th>
    <th>Price</th>
    <th>Description</th>
   </tr>



    <tr>
        <th>  <img className='admin-image' src={product.image} alt="" /></th>
     <th>{product.name}</th>
      <td >  {product.price}</td>
      <td> {product.description}</td>
      <button type="button" class="admin-btn btn btn-light" onClick={()=>Delete(product._id)} > Delete</button>      
    </tr>

</table>
           </div>
        )
       })}
        </div>
        </div>


        <div className="admin-right"><div className="form">
       <input type="text" onChange={(e)=>{setName(e.target.value)}} name='name' placeholder='name' />
        <input type="number" onChange={(e)=>{setprice(e.target.value)}} name='price' placeholder='price'  />
        <input type="text" onChange={(e)=>{setImage(e.target.value)}} name='image' placeholder='image URL'  />
        <input type="text" onChange={(e)=>{setDescription(e.target.value)}} name='description' placeholder='description' />
      <button onClick={Handlesubmit} type="button" class="btn1 btn btn-outline-dark">Checkout</button>
       </div></div>
       
       
     
      
       
       
    </div>
  )
}

export default Admin
