import axios from 'axios';
import React,{useState,useEffect} from 'react'
import "../CSS/Admin.css";
function Admin() {
    const [name, setName] = useState("")
    const [price, setprice] = useState("");
    const [image, setImage] = useState("")
    const [description, setDescription] = useState("")
    const [products, setProducts] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:5000/api/Product').then((res)=>{
        setProducts(res.data)
        console.log(products)
      })
    }, [])
    

    const Handlesubmit =()=>{
        axios.post('http://localhost:5000/api/Product',{
            name,
            price,
            image,
            description
        }).then((res)=>{
            console.log(res)
        })
    }

    const Delete = (id)=>{
        axios.delete(`http://localhost:5000/api/Product/${id}`).thes((res)=>{
            window.location.reload();
        })
    }

  return (
    <div className='admin-container'>

        <div className='admin-left'>
        
        <div>
        {products.map((product)=>{
        return(
            <div>
                <table class="table table-dark">
  <thead>
   <tr>
    <th>Image</th>
    <th>Product Name</th>
    <th>Price</th>
    <th>Description</th>
   </tr>
  </thead>
  <tbody>

    <tr>
        <th>  <img src={product.image} alt="" /></th>
      <th scope="row">3</th>
      <td colspan="2" class="table-active">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
                {product.name}
              
                {product.price}
                {product.description}
                <button onClick={()=>Delete(product._id)} > Delete</button>
            </div>
        )
       })}
        </div>
        </div>


        <div className="admin-right"><div className="form">
       <input type="text" onChange={(e)=>{setName(e.target.value)}} name='name' placeholder='name' />
        <input type="number" onChange={(e)=>{setprice(e.target.value)}} name='price' placeholder='price'  />
        <input type="text" onChange={(e)=>{setImage(e.target.value)}} name='image' placeholder='image URL'  />
        <input type="text" onChange={(e)=>{setDescription(e.target.value)}} name='description' placeholder='description' />
      <button onClick={Handlesubmit} type="button" class="btn1 btn btn-outline-dark">Checkout</button>
       </div></div>
       
       
     
      
       
       
    </div>
  )
}

export default Admin
