import React,{useState} from 'react'
import { useGetAllProductsQuery } from '../features/productAPI'
import "../CSS/Homescreen.css"
import { useDispatch } from 'react-redux'
import { addTocart } from '../features/CartSlice'


function Homescreen() {
  const { data, error, isLoading } = useGetAllProductsQuery()
  const dispatch = useDispatch();
  const [filteredproduct, setFilteredproduct] = useState([]);
  const [searchinput, setSearchinput] = useState("")
const [productlist, setProductlist] = useState([])

  const addtocart = (product) => {
    dispatch(addTocart(product));
    setProductlist(product)
  }

const searchItems = (searchValues)=>{
  setSearchinput(searchValues);
  if(searchinput !==""){
    const filteredproduct = productlist.filter((list)=>{
      return Object.values(list.name)
      .join("")
      .toLowerCase()
      .in
    })
  }
}

  return (
    <div className='main-home-container'>
      <div className="search-head">
        <div className='list-search'>
          <li> <a href=""></a> Men</li>
          <li> <a href=""></a> Men</li>
          <li> <a href=""></a> Men</li>
          <li> <a href=""></a> Men</li>
        </div>
        <input type="search" placeholder='Search' onChange={(e) => searchItems(e.target.value)} name="searchitem"  />
      </div>

      <div className='main-home' >
        {isLoading ? (<p>Loading</p>) : error ? (<p>An Error Occured</p>) : (
          <>
            {data?.map(product =>
              <div class="card" key={product._id}>
                <img src={product.image} alt="" className='img-fluid' />
                <div class="card-body">
                  <h5 class="card-title">{product.name}</h5>
                  <p class="card-text">${product.price}</p>
                  <button type="button" class="btn1 btn btn-outline-dark" onClick={() => addtocart(product)}>Cart</button>
                </div>
              </div>
            )}

          </>
        )}

      </div>
    </div>



  )





}

export default Homescreen
