import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
    items: [],
    status: null
};

export const productFetch = createAsyncThunk(
    "products/productFetch",
    
)

const productSlice = createSlice({
    name: "products",
    initialState,
    reducers: {}
});

export default productSlice.reducer
