import axios from 'axios';
import React,{useState} from 'react'

function Admin() {
    const [name, setName] = useState('')
    const [price, setprice] = useState('');
    const [image, setImage] = useState('')
    const [description, setDescription] = useState('')

    const Handlesubmit =()=>{
        axios.post('http://localhost:5000/api/Product',{
            name
        })
    }

  return (
    <div>
        <input type="text" onChange={(e)=>setName(e.target.name)} name='name' placeholder='name' />
        <input type="number" onChange={(e)=>setprice(e.target.price)} name='price' placeholder='price' />
        <input type="text" onChange={(e)=>setImage(e.target.image)} name='image' placeholder='image' />
        <input type="text" onChange={(e)=>setDescription(e.target.description)} name='description' placeholder='description' />
        <button onClick={Handlesubmit}></button>
    </div>
  )
}

export default Admin
