import axios from 'axios';
import React,{useState,useEffect} from 'react'

function Admin() {
    const [name, setName] = useState("")
    const [price, setprice] = useState("");
    const [image, setImage] = useState("")
    const [description, setDescription] = useState("")
    const [products, setProducts] = useState([]);

    useEffect(() => {
      axios.get('http://localhost:5000/api/Product').then((res)=>{
        setProducts(res.data)
        console.log(products)
      })
    }, [])
    

    const Handlesubmit =()=>{
        axios.post('http://localhost:5000/api/Product',{
            name,
            price,
            image,
            description
        }).then((res)=>{
            console.log(res)
        })
    }

    const Delete = (id)=>{
        axios.delete(``)
    }

  return (
    <div>
       
     
        <input type="text" onChange={(e)=>{setName(e.target.value)}} name='name' placeholder='name' />
        <input type="number" onChange={(e)=>{setprice(e.target.value)}} name='price' placeholder='price'  />
        <input type="text" onChange={(e)=>{setImage(e.target.value)}} name='image' placeholder='image URL'  />
        <input type="text" onChange={(e)=>{setDescription(e.target.value)}} name='description' placeholder='description' />
        <button onClick={Handlesubmit}>List</button>
        <div>
        {products.map((product)=>{
        return(
            <div>
                {product.name}
                <img src={product.image} alt="" />
                {product.price}
                {product.description}
                <button onClick={()=>Delete(product._id)} > Delete</button>
            </div>
        )
       })}
        </div>
       
    </div>
  )
}

export default Admin
